from flask_sqlalchemy import SQLAlchemydb = SQLAlchemy()class Department(db.Model):    __tablename__ = 'department'    dept_id = db.Column(db.Integer, primary_key=True)    dept_name = db.Column(db.String, nullable=False)    class Employee(db.Model):    __tablename__ = 'employee'    uniq_id = db.Column(db.String, primary_key=True)    dept_id = db.Column(db.Integer,                        db.ForeignKey('department.dept_id'),                        nullable=False)    